class ToTop extends HTMLElement{connectedCallback(){window.innerWidth<=600?this.innerHTML+='\n      <a href="#">\n        <span class="badge arrow-up">⬆</span>\n      </a>\n      ':this.innerHTML+='\n      <a href="#">\n        <img src="./assets/arrow-up.png" alt="Scroll to top" title="Scroll to top" aria-label="Scroll to top" class="badge arrow-up" />\n      </a>\n      '}}customElements.define("to-top",ToTop);let experiencesText=document.querySelector("#about .card-text");experiencesText.innerHTML=(new Date).getFullYear()-2019+experiencesText.innerHTML;const toTop=document.querySelector("#about").appendChild(document.createElement("to-top")).querySelector("a");window.addEventListener("scroll",(()=>{window.pageYOffset>450?toTop.classList.add("active"):toTop.classList.remove("active")}));const footerContainer=document.querySelector("#footerContainer"),year=(new Date).getFullYear();function footerContainerF(e){null!=e&&(e.innerHTML+=`Copyright © ${year} Vladislav Kazantsev. <span class="nowrap">All Rights Reserved.</span>`)}footerContainerF(footerContainer);
