class ToTop extends HTMLElement{connectedCallback(){window.innerWidth<=600?this.innerHTML+='\n      <a href="#">\n        <span class="badge arrow-up">⬆</span>\n      </a>\n      ':this.innerHTML+='\n      <a href="#">\n        <img src="./assets/arrow-up.png" alt="Scroll to top" title="Scroll to top" aria-label="Scroll to top" class="badge arrow-up" />\n      </a>\n      '}}customElements.define("to-top",ToTop);const toTop=document.querySelector("#about").appendChild(document.createElement("to-top")).querySelector("a");function footerContainerF(e){null!=e&&(e.innerHTML+='\nCopyright © 2024 Vladislav Kazantsev. <span class="nowrap">All Rights Reserved.</span>\n      ')}window.addEventListener("scroll",(()=>{window.pageYOffset>450?toTop.classList.add("active"):toTop.classList.remove("active")})),footerContainer=document.querySelector("#footerContainer"),footerContainerF(footerContainer);
