document.addEventListener("DOMContentLoaded",(()=>{const t=document.querySelector(".email");if(!t)return;const e=document.createElement("div");e.classList.add("wrapper");const n=document.createElement("button");n.classList.add("copy-button"),n.textContent="Copy Email",n.addEventListener("click",(async()=>{try{await navigator.clipboard.writeText(t.querySelector("a").textContent),n.textContent="Copied ✓",n.classList.add("copied"),setTimeout((()=>{n.textContent="Copy Email",n.classList.remove("copied")}),1700)}catch(t){alert("Clipboard unavailable")}})),e.appendChild(n),t.appendChild(e);const a=document.querySelector(".phone");if(!a)return;const o=document.createElement("div");o.classList.add("wrapper");const c=document.createElement("button");c.classList.add("copy-button"),c.textContent="Copy Phone",c.addEventListener("click",(async()=>{try{await navigator.clipboard.writeText(a.querySelector("a").textContent),c.textContent="Copied ✓",c.classList.add("copied"),setTimeout((()=>{c.textContent="Copy Phone",c.classList.remove("copied")}),1700)}catch(t){alert("Clipboard unavailable")}})),o.appendChild(c),a.appendChild(o)}));
