const projectCollection=document.querySelector(".project-collection");fetch("/index.html").then((e=>e.text())).then((e=>{const t=(new DOMParser).parseFromString(e,"text/html");Array.from(t.querySelectorAll(".project-card")).forEach((e=>{const t=e.querySelector(".project-title").innerHTML.replace(/<br\s*\/?>/gi," ").trim(),r=e.querySelector(".button-container > a:nth-of-type(1)").href,o=e.querySelector(".button-container > a:nth-of-type(2)").href,n=document.createElement("li");n.innerHTML=`${t}:\n      <li><a href=${r} target="_blank" rel="noopener noreferrer">GitHub</a></li>\n      <li><a href=${o} target="_blank" rel="noopener noreferrer">Live Demo</a></li>\n      `,projectCollection.appendChild(n)}))})).catch(console.error),document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();const t=this.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth",block:"start"})}))}));
