const projectCollection=document.querySelector(".project-collection");fetch("https://raw.githubusercontent.com/PrimeSolar/web-developer/refs/heads/main/index.html").then((e=>e.text())).then((e=>{const r=(new DOMParser).parseFromString(e,"text/html");Array.from(r.querySelectorAll(".project-card")).forEach((e=>{const r=e.querySelector(".project-title").innerHTML.replace(/<br\s*\/?>/gi," ").trim(),t=e.querySelector(".button-container > a:nth-of-type(1)").href,o=e.querySelector(".button-container > a:nth-of-type(2)").href,n=document.createElement("li");n.innerHTML=`${r}:\n      <li><a href=${t} rel="noopener noreferrer">GitHub</a></li>\n      <li><a href=${o} rel="noopener noreferrer">Live Demo</a></li>\n      `,projectCollection.appendChild(n)}))})).catch(console.error),document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();const r=this.getAttribute("href");document.querySelector(r).scrollIntoView({behavior:"smooth",block:"start"})}))}));
